
public class ifelse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 11;
		//if'in parantez içerisindeki þartý saðlanýrsa, süslü parantez içerisindeki iþlemler yapýlýr
		if(a > 5 && a<10)
		{
			System.out.println("a 5 ile 10 arasýndadýr");
			
		}
		//ancak if'in þartý saðlanmazsa else'in içerisindeki iþlemler yapýlýr
		else
		{
			System.out.println("a 5 ile 10 arasýnda deðildir");
		}
		
	}

}
